module Chapter4 where

type Nat = Int -- to avoid precission errors use Integer

-- ONE-DIMENSIONAL SEARCH PROBLEM

linearSearch :: (Nat -> Nat) -> Nat -> [Nat]
linearSearch f t = seek (0, t)
  where
    seek (a, b) = [x | x <- [a .. b], t == f x]

search1D :: (Nat -> Nat) -> Nat -> [Nat]
search1D f t = seek (0, t)
  where
    seek (a, b)
      | a > b = []
      | t < f m = seek (a, m - 1)
      | t == f m = [m]
      | otherwise = seek (m + 1, b)
      where
        m = choose (a, b)
    choose (a, b) = (a + b) `div` 2

bound :: (Nat -> Nat) -> Nat -> (Nat, Nat)
bound f t = if t <= f 0 then (-1, 0) else (b `div` 2, b)
  where
    b = until done (* 2) 1
    done b = t <= f b

search1D' :: (Nat -> Nat) -> Nat -> [Nat]
search1D' f t = [x | x == t]
  where
    x = smallest (bound f t) f t

smallest :: (Nat, Nat) -> (Nat -> Nat) -> Nat -> Nat
smallest (a, b) f t
  | a + 1 == b = b
  | t <= f m = smallest (a, m) f t
  | otherwise = smallest (m, b) f t
  where
    m = (a + b) `div` 2

-- TWO-DIMENSIONAL SEARCH PROBLEM

quadraticSearch :: ((Nat, Nat) -> Nat) -> Nat -> [(Nat, Nat)]
quadraticSearch f t = [(x, y) | x <- [0 .. t], y <- [0 .. t], t == f (x, y)]

quadraticSearch' :: ((Nat, Nat) -> Nat) -> Nat -> [(Nat, Nat)]
quadraticSearch' f t = [(x, y) | x <- [0 .. t], y <- [t, t -1 .. 0], t == f (x, y)]

--searchIn :: (Nat, Nat) -> ((Nat, Nat) -> Nat) -> Nat -> [(Nat, Nat)]
--searchIn (a, b) f t = [(x, y) | x <- [a .. t], y <- [b, b -1 .. 0], t == f (x, y)]

search2D :: ((Nat, Nat) -> Nat) -> Nat -> [(Nat, Nat)]
search2D f t = searchIn (0, t)
  where
    searchIn (x, y)
      | x > t || y < 0 = []
      | z < t = searchIn (x + 1, y)
      | z == t = (x, y) : searchIn (x + 1, y - 1)
      | z > t = searchIn (x, y - 1)
      where
        z = f (x, y)

f :: (Nat, Nat) -> Nat
f (x, y) = x ^ 2 + 3 ^ y

-- >>> f (24,9)
-- 20259

-- >>> f (0, 20259)
-- 

-- >>>
-- >>> search2D (\(x, y) -> x^2 + 3^y) 20259
-- [(24,9)]

search2D' :: ((Nat, Nat) -> Nat) -> Nat -> [(Nat, Nat)]
search2D' f t = from (0, p) (q, 0)
  where
    p = smallest (-1, t) (\y -> f (0, y)) t
    q = smallest (-1, t) (\x -> f (x, 0)) t
    from (x1, y1) (x2, y2)
      | x2 < x1 || y1 < y2 = []
      | y1 - y2 <= x2 - x1 = row x
      | otherwise = col y
      where
        x = smallest (x1 - 1, x2) (\x -> f (x, r)) t
        y = smallest (y2 - 1, y1) (\y -> f (c, y)) t
        c = (x1 + x2) `div` 2
        r = (y1 + y2) `div` 2
        row x
          | z < t = from (x1, y1) (x2, r + 1)
          | z == t = (x, r) : from (x1, y1) (x - 1, r + 1) ++ from (x + 1, r - 1) (x2, y2)
          | z > t = from (x1, y1) (x - 1, r + 1) ++ from (x, r - 1) (x2, y2)
          where
            z = f (x, r)
        col y
          | z < t = from (c + 1, y1) (x2, y2)
          | x == t = (c, y) : from (x1, y1) (c - 1, y + 1) ++ from (c + 1, y - 1) (x2, y2)
          | z > t = from (x1, y1) (c - 1, y) ++ from (c + 1, y - 1) (x2, y2)
          where
            z = f (c, y)

-- >>> search2D (\(x, y) -> x^2 + 3^y) 20259
-- [(24,9)]

-- BINARY SEARCH-TREES

-- Unbalanced
data Tree' a = Null' | Node' (Tree' a) a (Tree' a)

size' :: Tree' a -> Nat
size' Null' = 0
size' (Node' l _ r) = 1 + size' l + size' r

flatten' :: Tree' a -> [a]
flatten' Null' = []
flatten' (Node' l x r) = flatten' l ++ [x] ++ flatten' r

search' :: Ord k => (a -> k) -> k -> Tree' a -> Maybe a
search' key k Null' = Nothing
search' key k (Node' l x r)
  | key x < k = search' key k r
  | key x == k = Just x
  | key x > k = search' key k l

height' :: Tree' a -> Nat
height' Null' = 0
height' (Node' l _ r) = 1 + max (height' l) (height' r)

mktree' :: Ord a => [a] -> Tree' a
mktree' [] = Null'
mktree' (x : xs) = Node' (mktree' ys) x (mktree' zs)
  where
    (ys, zs) = partition (< x) xs
    partition p xs = (filter p xs, filter (not . p) xs)

-- Balanced
data Tree a = Null | Node Nat (Tree a) a (Tree a) deriving (Show)

height :: Tree a -> Nat
height Null = 0
height (Node h _ _ _) = h

node :: Tree a -> a -> Tree a -> Tree a
node l x r = Node h l x r where h = 1 + max (height l) (height r)

mktree :: Ord a => [a] -> Tree a
mktree = foldr insert Null

insert :: Ord a => a -> Tree a -> Tree a
insert x Null = node Null x Null
insert x (Node h l y r)
  | x < y = balance (insert x l) y r
  | x == y = Node h l y r
  | y < x = balance l y (insert x r)

bias :: Tree a -> Int
bias (Node _ l x r) = height l - height r

balance :: Tree a -> a -> Tree a -> Tree a
balance t1 x t2
  | abs (h1 - h2) <= 1 = node t1 x t2
  | h1 == h2 + 2 = rotateR t1 x t2
  | h2 == h1 + 2 = rotateL t1 x t2
  where
    h1 = height t1
    h2 = height t2

rotr :: Tree a -> Tree a
rotr (Node _ (Node _ ll y rl) x r) = node ll y (node rl x r)

rotl :: Tree a -> Tree a
rotl (Node _ ll y (Node _ lrl z rrl)) = node (node ll y lrl) z rrl

rotateR :: Tree a -> a -> Tree a -> Tree a
rotateR t1 x t2 =
  if 0 <= bias t1
    then rotr (node t1 x t2)
    else rotr (node (rotl t1) x t2)

rotateL :: Tree a -> a -> Tree a -> Tree a
rotateL t1 x t2 =
  if bias t2 <= 0
    then rotl (node t1 x t2)
    else rotl (node t1 x (rotr t2))

balanceR :: Tree a -> a -> Tree a -> Tree a
balanceR (Node _ l y r) x t2 =
  if height r >= height t2 + 2
    then balance l y (balanceR r x t2)
    else balance l y (node r x t2)

-- Exercise 4.16
balanceL :: Tree a -> a -> Tree a -> Tree a
balanceL t1 x (Node _ l y r) =
  if height l >= height t1 + 2
    then balance (balanceL t1 x l) y r
    else balance (node t1 x l) y r

gbalance :: Tree a -> a -> Tree a -> Tree a
gbalance t1 x t2
  | abs (h1 - h2) <= 2 = balance t1 x t2
  | h1 > h2 + 2 = balanceR t1 x t2
  | h1 + 2 < h2 = balanceL t1 x t2
  where
    h1 = height t1
    h2 = height t2

flatten :: Tree a -> [a]
flatten = go []
  where
    go acc Null = acc
    go acc (Node _ l x r) = go (x : go acc r) l

sort :: (Ord a) => [a] -> [a]
sort = flatten . mktree

-- >>> sort [3,2,5,1,4]
-- [1,2,3,4,5]
